pragma solidity 0.8.20;

import { MerkleProof } from "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * @title AirdropCommons
 * @notice Extending this contract allows you to deploy a contract which, through a Merkle Tree,
 * allows users to claim their preassigned tokens balances.
 * @dev It's compatible with the tool "openzeppelin/merkle-tree" that generates the merkle tree given
 *      the data (root and proofs)
 */
abstract contract AirdropCommons {
    mapping(bytes32 => bool) public claimedLeaf;

    // Amount claimed up to this point
    uint256 public totalClaimed;

    // (account,amount) Merkle Tree root
    bytes32 public root;
    // The Token to be airdropped
    IERC20 public token;

    error InvalidProof();
    error UsedLeaf();

    /**
     * @notice constructor
     * @param token_ the ERC20 like token to be airdrop
     * @param root_ (account,amount) Merkle Tree root
     */
    function __private_AirdropCommons_init(IERC20 token_, bytes32 root_) internal {
        root = root_;
        token = token_;
    }

    // --------------- Abstract Functions ----------------------

    /**
     * @notice assigns the Token to the new `origin_`
     */
    function _assignToken(address origin_, uint256 amount_, address executor_, address recipient_) internal virtual;

    // --------------- Public Functions ----------------------

    /**
     * @notice claims the sender tokens
     * @dev The leaf hashing process is compatible with the tool "openzeppelin/merkle-tree" as it is,
     *          ideally it should not be changed
     * @param amount_ amount of tokens to be claim, needs to match MT leaf
     * @param proof_ bytes32's array to prove that the claim belongs to the merkle tree
     *           Use "openzeppelin/merkle-tree" to generate the tree and its proofs
     */
    function claim(uint256 amount_, bytes32[] calldata proof_) external {
        _claim(msg.sender, msg.sender, amount_, proof_);
    }

    // ------- Internal Functions -------

    /**
     * @notice claims the `origin_` tokens and transfer them to recipient_
     * @dev The leaf hashing process is compatible with the tool "openzeppelin/merkle-tree" as it is,
     *          ideally it should not be changed
     * @param origin_ of the proof
     * @param recipient_ address to transfer the tokens to
     * @param amount_ amount of tokens to be claim, needs to match MT leaf
     * @param proof_ bytes32's array to prove that the claim belongs to the merkle tree
     *           Use "openzeppelin/merkle-tree" to generate the tree and its proofs
     */
    function _claim(address origin_, address recipient_, uint256 amount_, bytes32[] calldata proof_) internal {
        // Hashing twice to be compatible with OZ (also guarantees that we are not vulnerable to reinterpreting
        // an intermediate node as a leaf https://github.com/OpenZeppelin/openzeppelin-contracts/issues/3091)
        bytes32 leaf = keccak256(bytes.concat(keccak256(abi.encode(origin_, amount_))));

        if (!MerkleProof.verifyCalldata(proof_, root, leaf)) revert InvalidProof();
        if (claimedLeaf[leaf]) revert UsedLeaf();
        claimedLeaf[leaf] = true;

        unchecked {
