pragma solidity 0.8.20;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { AirdropCommons } from "./AirdropCommons.sol";

/**
 * @title AirdropGovernance
 * @notice This contract allows you to deploy a contract which, through a Merkle Tree,
 * allows users to claim their preassigned tokens balances.
 * @dev It's compatible with the tool "openzeppelin/merkle-tree" that generates the merkle tree given
 *      the data (root and proofs)
 */
contract AirdropGovernance is AirdropCommons {
    event TokensClaimed(address indexed origin_, uint256 amount_, address executor_, address recipient_);

    /**
     * @notice constructor
     * @param token_ the ERC20 like token to be airdrop
     */
    constructor(IERC20 token_, bytes32 root_) {
        __private_AirdropCommons_init(token_, root_);
    }

    // ------- Internal Override Functions -------

    /**
     * @notice assigns the Token to the `recipient_`
     */
    function _assignToken(address origin_, uint256 amount_, address executor_, address recipient_) internal override {
        emit TokensClaimed(origin_, amount_, executor_, recipient_);
        SafeERC20.safeTransfer(token, recipient_, amount_);
    }
}
